<script>import { transformProps } from "@unpic/core";
import styleToCss from "style-object-to-css-string";
$:
  ({ style: parentStyle, ...props } = $$props);
$:
  ({
    alt,
    style: styleObj,
    ...transformedProps
  } = transformProps({ ...props, style: {} }));
$:
  style = [styleToCss(styleObj), parentStyle].filter(Boolean).join(";");
</script>

<img {alt} {style} {...transformedProps} />
