<script>import { onMount } from "svelte";
import { triggerOnIdle, invariant } from "../util.js";
invariant(!($$props.uploadPreset == void 0 && $$props.signatureEndpoint == void 0), "You need to pass at least of of the following props: `uploadPreset` or `signatureEndpoint`");
invariant(!($$props.uploadPreset != void 0 && $$props.signatureEndpoint != void 0), "You can only pass one of the following props: `uploadPreset` or `signatureEndpoint`");
$: ({ uploadPreset, signatureEndpoint, onError, onUpload, options, onOpen, onClose } = $$props);
let cloudinary;
let widget;
const signed = !!signatureEndpoint;
const WIDGET_WATCHED_EVENTS = ["success", "display-changed"];
let error = void 0;
let results = void 0;
let isLoading = true;
const uploadOptions = {
    cloudName: import.meta.env.VITE_PUBLIC_CLOUDINARY_CLOUD_NAME,
    uploadPreset: uploadPreset || import.meta.env.VITE_PUBLIC_CLOUDINARY_UPLOAD_PRESET,
    apiKey: import.meta.env.VITE_PUBLIC_CLOUDINARY_API_KEY,
    ...options
};
if (signed) {
    uploadOptions.uploadSignature = generateSignature;
    if (!uploadOptions.apiKey) {
        console.warn(`Missing dependency: Signed Upload requires an API key`);
    }
}
function handleOnLoad() {
    isLoading = false;
    if (!cloudinary) {
        cloudinary = window.cloudinary;
    }
    triggerOnIdle(() => {
        if (!widget) {
            widget = createWidget();
        }
    });
}
function generateSignature(callback, paramsToSign) {
    if (typeof signatureEndpoint === "undefined") {
        throw Error("Failed to generate signature: signatureEndpoint undefined.");
    }
    fetch(signatureEndpoint, {
        method: "POST",
        body: JSON.stringify({
            paramsToSign
        })
    }).then((r) => r.json()).then(({ signature }) => {
        callback(signature);
    });
}
function createWidget() {
    return cloudinary?.createUploadWidget(uploadOptions, (uploadError, uploadResult) => {
        if (typeof uploadError !== "undefined") {
            error = uploadError;
        }
        if (WIDGET_WATCHED_EVENTS.includes(uploadResult?.event)) {
            results = uploadResult;
        }
    });
}
function open() {
    if (!widget) {
        widget = createWidget();
    }
    widget?.open();
    if (typeof onOpen === "function") {
        onOpen(widget);
    }
}
function onLoadingError() {
    console.error(`Failed to load Cloudinary Upload Widget`);
}
$: {
    if (results != null) {
        const isSuccess = results.event === "success";
        const isClosed = results.event === "display-changed" && results.info === "hidden";
        if (isSuccess && typeof onUpload === "function") {
            onUpload(results, widget);
        }
        if (isClosed && typeof onClose === "function") {
            onClose(widget);
        }
    }
}
$: {
    if (error && typeof onError === "function") {
        onError(error, widget);
    }
}
onMount(() => {
    handleOnLoad();
});
</script>

<svelte:head>
	<script
		id={`cloudinary-uploadwidget-${Math.floor(Math.random() * 100)}`}
		src="https://widget.cloudinary.com/v2.0/global/all.js"
		on:error={onLoadingError}
	></script>
</svelte:head>

